import React, { Component } from 'react';
import logo from '../logo.png';
import './App.css';
import Web3 from 'web3';
import OrderLogic from '../abis/OrderLogicContract.json';
import {storeAPI} from '../StoreAPI';



class App extends Component {
  // Code


  async componentWillMount() {
    await this.loadWeb3()
    await this.loadBlockchainData()
  }


  async loadWeb3() {
    if (window.ethereum) {
      window.web3 = new Web3(window.ethereum)
      await window.ethereum.enable()
    }
    else if (window.web3) {
      window.web3 = new Web3(window.web3.currentProvider)
    }
    else {
      window.alert('Non-Ethereum browser detected. You should consider trying MetaMask!')
    }
  }


    async loadBlockchainData() {
      const web3 = window.web3
      // Load account
      const accounts = await web3.eth.getAccounts()
      this.setState({ account: accounts[0] })
      // Network ID
      const networkId = await web3.eth.net.getId()
      const networkData = OrderLogic.networks[networkId]
      if(networkData) {
        const orderLogic = new web3.eth.Contract(OrderLogic.abi, networkData.address)
        this.setState({ orderLogic })
        console.log( "Contract" )
        console.log( orderLogic );
        const orderCount = await orderLogic.methods.orderCount().call()
        console.log( "Order count" )
        console.log( orderCount )

      } else {
        window.alert('SocialNetwork contract not deployed to detected network.')
      }
    }

    async createOrder(){
      console.log( "Create order called" )
      console.log( storeAPI().getOrders() )

      //this.state.socialNetwork.methods.createPost( this.setState.account,
      //                                              storeAPI.storeAdress).send({ from: this.state.account })
    }


    constructor(props) {
      super(props)

      this.state = {
        account: '',
        orderLogic: null,
        postCount: 0,
        posts: [],
        storeInformation:{},
        loading: true,
        store: storeAPI
      }

      this.createOrder = this.createOrder.bind(this)


    }


    render() {
    return (
      <div>
        <nav className="navbar navbar-dark fixed-top bg-dark flex-md-nowrap p-0 shadow">
          <a
            className="navbar-brand col-sm-3 col-md-2 mr-0"
            href="http://www.dappuniversity.com/bootcamp"
            target="_blank"
            rel="noopener noreferrer"
          >
            Order contract
          </a>
        </nav>
        <div className="container-fluid mt-5">
          <div className="row">
            <main role="main" className="col-lg-12 d-flex text-left">
              <div className="content mr-auto ml-auto">
                <h1>Order Contract</h1>

                <h2>Order #278 <span className="badge bg-primary text-light">Customer</span></h2>

                <table className="table">
                  <tbody>
                  <tr>
                    <th scope="row">Store adress:</th>
                    <td>0x963674c7428be89fec527b7543383b47798b81a153a882786ef80d052b6210bc</td>
                  </tr>
                  <tr>
                    <th scope="row">Customer adress:</th>
                    <td>0x963674c7428be89fec527b7543383b47798b81a153a882786ef80d052b6210bc</td>
                  </tr>
                  <tr>
                    <th scope="row">Deliverer adress:</th>
                    <td colSpan="2">0x963674c7428be89fec527b7543383b47798b81a153a882786ef80d052b6210bc</td>
                  </tr>
                  <tr>
                    <th scope="row">Order State</th>
                    <td class="table-info" colSpan="2">PAYED</td>
                  </tr>
                  </tbody>
                </table>

                <h3>Products <span className="badge bg-primary text-light">2</span></h3>

                <table className="table">
                  <tbody>
                  <tr>
                    <th scope="row">Name:</th>
                    <td></td>
                  </tr>
                  <tr>
                    <th scope="row">Description:</th>
                    <td>0x963674c7428be89fec527b7543383b47798b81a153a882786ef80d052b6210bc</td>
                  </tr>
                  <tr>
                    <th scope="row">Tax:</th>
                    <td colSpan="2">0x963674c7428be89fec527b7543383b47798b81a153a882786ef80d052b6210bc</td>
                  </tr>
                  <tr>
                    <th scope="row">Quantity</th>
                    <td colSpan="2">PAYED</td>
                  </tr>
                  <tr>
                    <th scope="row">Cost</th>
                    <td className="table-danger" colSpan="2">PAYED</td>
                  </tr>
                  </tbody>
                </table>

                <h3>Actions </h3>

                <p>
                <button type="button" className="btn btn-primary" onClick={this.createOrder}>Primary</button>
                </p>


              </div>
            </main>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
