import React, { Component } from 'react';
import logo from '../logo.png';
import './App.css';
import Web3 from 'web3';
import OrderLogic from '../abis/OrderLogicContract.json';
import { storeAPI } from '../StoreAPI';
import NonCreatedOrderTable from "./NonCreatedOrderTable";
import OrderInfo from "./OrderInfo";
import Actions from "./Actions";

class App extends Component {
  // Code

  async componentWillMount() {
    await this.loadWeb3()
    await this.loadBlockchainData()
    await this.getOrder()
    this.watchOrderEvents()
  }


  async loadWeb3() {
    if (window.ethereum) {
      window.web3 = new Web3(window.ethereum)
      await window.ethereum.enable()
    }
    else if (window.web3) {
      window.web3 = new Web3(window.web3.currentProvider)
    }
    else {
      window.alert('Non-Ethereum browser detected. You should consider trying MetaMask!')
    }
  }


    async loadBlockchainData() {
      const web3 = window.web3
      // Load account
      const accounts = await web3.eth.getAccounts()
      this.setState({ account: accounts[0] })
      // Network ID
      const networkId = await web3.eth.net.getId()
      const networkData = OrderLogic.networks[networkId]
      if(networkData) {
        const orderLogic = new web3.eth.Contract(OrderLogic.abi, networkData.address)
        this.setState({ orderLogic })
        console.log( "Contract" )
        console.log( orderLogic );
        const orderCount = await orderLogic.methods.orderCount().call()
        console.log( "Order count" )
        console.log( orderCount )

      } else {
        window.alert('SocialNetwork contract not deployed to detected network.')
      }
    }

    async createOrder(){
      console.log( "Create order called" )
      console.log( storeAPI.getOrder() )
      let order = storeAPI.getOrder()

      this.state.orderLogic.methods.createOrder( this.state.account, storeAPI.storeAdress,
                                                   order.deliveryAdress, "45678907656789765",
                                                   order.products.length, order.cost )
          .send({ from: this.state.account }).once( 'receipt', (receipt)  => {
            console.log("Order created")
            console.log(receipt)
            console.log("Order Events")
            console.log(receipt.events)
          }
          )
    }

    watchOrderEvents(){
      // this.state.orderLogic.OrderStateChanged.watch((error, result) => {
      //   console.log( "Get Event" );
      //   console.log( result );
      // })
    }

    async getOrder(){
      const order = await this.state.orderLogic.methods.orders(1).call()
      console.log( "Order" )
      console.log( order )
      if ( order.id == "0" ){ // order is not created
          console.log("Order is not created")

      }
    }


    constructor(props) {
      super(props)

      this.state = {
        account: '',
        orderLogic: null,
        postCount: 0,
        posts: [],
        storeInformation:{},
        loading: true,
        orderState: ""
        //store: storeAPI
      }

      this.createOrder = this.createOrder.bind(this)


    }


    render() {
    return (
      <div>

        <nav className="navbar navbar-dark fixed-top bg-dark flex-md-nowrap p-0 shadow">
          <a
            className="navbar-brand col-sm-3 col-md-2 mr-0"
            href="http://www.dappuniversity.com/bootcamp"
            target="_blank"
            rel="noopener noreferrer"
          >
            Order contract
          </a>
        </nav>

        <div className="container-fluid mt-5">
          <div className="row">
            <main role="main" className="col-lg-12 d-flex text-left">
              <div className="content mr-auto ml-auto">
                {/* Here will be main content */}
                <NonCreatedOrderTable/>
                <OrderInfo/>
                <Actions/>
              </div>
            </main>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
