                    "cost": 2,
                    "refundable": true,
                    "tax": "20",
                    "quantity": 1,
                    "returned": false
                },
                { "id": "27362973",
                    "name":"Custom Belt",
                    "description": "Nice custom made belt.",
                    "cost": 2,
                    "refundable": false,
                    "tax": "20",
                    "quantity": 1,
                    "returned": true
                }
            ],
            "cost": 5,
            "delivererAddress" : "0x366F79C2dBC393e0F6c8dAEbddE932bD81e70299",
            "deliveryAddress" : "Cierne, U Flora 02312, Slovensko",
            "storeAdress": this.storeAdress
        }
    }
}


